import { Injectable } from '@angular/core';
import { Product} from './products';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class CartService {
  items: Product[] = [];
  constructor() { }


  addToCart(product:Product)
  {
    this.items.push(product);
  }

  getItem()
  {
    return this.items;

  }

  clearCart()
  {
    this.items = [];
    return this.items;

  }
}
